export const reducer = (state = "", action) => {
  switch (action.type) {
    case "newuser":
      return (state = action.payload);
    default:
      return state;
  }
};
export const mstreducer = (state = 0, action) => {
  switch (action.type) {
    case "INCREMENT":
      return (state = state + 1);
    case "RESET":
      return (state = 0);
    default:
      return state;
  }
};

export const guesses = (state = Set([]), action) => {
  switch (action.type) {
    case "CORRECT":
      return state.add(action.payload);
    default:
      return state;
  }
};
