import React, { useEffect, useState } from "react";

import UserSetup from "./UserSetup";
import { images } from "./images/images";

import { connect } from "react-redux";
import { fetchQuote, addGuess, addMiss } from "../redux/user/userActions";

function HangmanLogic({ userData, fetchQuote }) {
  const [currentKey, setCurrentKey] = useState("");
  const createKeys = () => {
    return "abcdefghijklmnoprstuvwzxyq".split("").map((letter) => (
      <button
        className="m-2 p-2 bg-white text-black rounded-full"
        key={letter}
        value={letter}
        onClick={handleGuess}
      >
        {letter}
      </button>
    ));
  };
  useEffect(() => {
    fetchQuote();
  }, []);
  const handleGuess = (e) => {
    //dodavanje novih elemenata u set([])
    //KORISTENJE SETA JER NEMA DUPLIKATA
    let guessedArray = userData.guessedKeys;
    guessedArray.add(e.target.value);
    //dodavanje pokrešaka i rerender stringa

    //addGuess(e.target.value);
    //console.log(userData.error);
    /*addMisstake(String(quote).toLowerCase().includes(e.target.value) ? 0 : 1);
    guessingGame();
    console.log(e.target.className);*/
  };
  const guessingGame = () => {
    let guessedLetters = userData.guessedKeys;

    let lines = userData.quote.split("").map((letter) => {
      if (/[a-zA-Z]/.test(letter)) {
        return guessedLetters.has(letter.toLowerCase()) ||
          guessedLetters.has(letter.toUpperCase())
          ? letter
          : " _ ";
      } else {
        return letter === " " ? "    " : letter;
      }
    });
    return lines;
  };

  return (
    <div className="bg-gray-900 text-white h-screen">
      {!userData.username ? (
        <UserSetup />
      ) : (
        <div>
          <div className="flex justify-center items-center pt-10">
            <img
              src={images[userData.misstakes]}
              className="w-1/5 "
              alt="hangmangame"
            />
            <div className="pl-10 ">
              <div>Username: {userData.username}</div>
              <div>Score: 0</div>
              <div>Time: 0</div>
              <div>Misstakes: {userData.misstakes} of 6</div>
            </div>
          </div>
          <div className="flex mt-5">
            <div className="w-1/3 p-5 m-auto bg-gray-900 text-center whitespace-pre-wrap">
              <div className=" align-center  text-[20px] ix:text-[12px] break-all">
                {userData.quote ? guessingGame() : "Loading ..."}
              </div>
            </div>
          </div>
          <div className="flex mt-5">
            <div className="m-auto bg-gray-900">
              <div className="grid grid-cols-7 text-center">{createKeys()}</div>
            </div>
          </div>
          <div className="w-screen mt-10 h-10 flex justify-center">
            <button className="w-1/5 bg-orange-400 rounded border-solid border-2 border-white shadow drop-shadow">
              {" "}
              RESET{" "}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    userData: state.user,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    fetchQuote: () => dispatch(fetchQuote()),
    addGuess: (letter) => dispatch(addGuess(letter)),
    addMiss: (value) => dispatch(addMiss(value)),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(HangmanLogic);

