import React from "react";
import { useEffect, useState } from "react";
import start0 from "./images/start0.png";
import { useSelector } from "react-redux";

export default function HangmanLogic() {
  const player = useSelector((state) => state.player);
  const [board, setBoard] = useState([]);

  const handleGuess = () => {};

  const setupBoard = () => {
    return "abcdefghijklmnoprstuvwzxy".split("").map((letter) => (
      <button
        className="btn btn-lg btn-primary m-2"
        key={letter}
        value={letter}
        onClick={this.handleGuess}
        disables={this.state.guessed.has(letter)}
      >
        {letter}
      </button>
    ));
  };

  useEffect(() => {
    setBoard(setupBoard());
  }, []);

  return (
    <div className="p-4 flex flex-grow justify-center object-center">
      <div className="text-white h-56 grid grid-cols-1 gap-4 content-start">
        <div>Username: {player} </div>
        <div>Score: 321</div>
        <div>Time: 456</div>
        <div>Misstakes: 0 of 6</div>
      </div>
      <div className="w-1/5">
        <img
          src={start0}
          className="ml-10 shadow rounded align-middle border-none"
          alt="hangmangame"
        />
      </div>
      <div className="text-white absolute mt-48">Guess sentence:</div>
      <div className="text-white absolute mt-52">
        ___ ____ _____ _____ ____ __{" "}
      </div>
      <div>{board}</div>
    </div>
  );
}
