import React from "react";
import { useEffect, useState } from "react";
import start0 from "./images/start0.png";
import start1 from "./images/start1.png";
import start2 from "./images/start2.png";
import start3 from "./images/start3.png";
import start4 from "./images/start4.png";
import start5 from "./images/start5.png";
import start6 from "./images/start6.png";
import win from "./images/win.png";
import lost from "./images/lost.png";

import { useSelector, useDispatch } from "react-redux";
import { bindActionCreators } from "redux";
import { actionCreators } from "../state/index";

import axios from "axios";

const api = axios.create({
  baseURL: "https://api.quotable.io/random",
});

export default function HangmanLogic() {
  const player = useSelector((state) => state.player);
  const misstakeCounter = useSelector((state) => state.misstakes);
  const quoteState = useSelector((state) => state.trueQuote);
  const guessedState = useSelector((state) => state.guessed);

  const [guessWords, setGuessWords] = useState("");

  const [board, setBoard] = useState([]);
  const [quote, setQuote] = useState("");

  const [missT, setMissT] = useState(0);
  const [gchars, setGchars] = useState(new Set([]));

  //LOCAL STATE CONSTS FOR SCREEN UPDATE
  const props = {
    maxWrong: 6,
    images: [start0, start1, start2, start3, start4, start5, start6, win, lost],
  };

  const dispatch = useDispatch();
  //REDUX FUNCTIONS FOR UPDATING STATES
  //ADDING MISSTAKE 0 OR 1
  //ADDING GUESS - ADDS LETTER TO SET OF GUESSED LETTERS
  //ADDQUOTE - ADDS QUOTE
  //RESETGUESS - CREATES NEW EMPTY SET ( NEW SET([]))
  //RESETMISSTAKES - CHANGES STATE TO 0
  const { addMisstake, addGuess, addQuote, resetGuess, resetMisstakes } =
    bindActionCreators(actionCreators, dispatch);

  //RUN ONCE THE PAGE LOADS AND DONT RUN AGAIN

  useEffect(() => {
    //SETTING UP LETTER BUTTON BOARD
    setBoard(createKeys());
    //GETTING RANDOM QUOTE FROM API
    const getQuote = async () => {
      let line = await api.get().then((res) => {
        return res.data.content;
      });
      setQuote(line);
    };
    getQuote();
    guessingGame();
  }, []);

  //FUNCTION FOR MAPPING OVER FETCHED SENTENCE AND RETURNING IT
  //AS "_" FOR LETTERS THAT HAVE NOT BEEN GUESSED
  //FIRST IF CHECK IF THE LETTER IS ALPHA BECAUSE OTHER SYMBOLS
  //DONT GET FLAGGED WITH "_"
  const guessingGame = () => {
    let lines = quote.split("").map((letter) => {
      if (/[a-zA-Z]/.test(letter)) {
        return gchars.has(letter.toLowerCase()) ||
          gchars.has(letter.toUpperCase())
          ? letter
          : " _ ";
      } else {
        return letter === " " ? "    " : letter;
      }
    });
    setGuessWords(lines);
    return lines;
  };

  //FUNCTION THAT GETS TRIGGERED BY BUTTON CLICK
  //IT PUSHES LETTER TO SET OF GUESSED LETTERS
  //IT ALSO SHOULD INCREMENT MISSTAKES IF LETTER IS NOT IN
  //QUOTE
  //DELAYED SETUP OF VALUES ?

  const CreateGuess = (e) => {
    e.target.className = e.target.className.replace("bg-white", "bg-red-700");
    let temp = gchars;
    temp.add(e.target.value);
    //LOCAL HOOKS THAT HOLD GUESSED LETTERS
    setGchars(temp);
    //REDUX STATE FUNCTION TO CHANGE SET AND UPDATE IT WITH NEW VALUE
    addGuess(e.target.value);
    //IT ALWAYS DOES STATE-1 FOR SOMEREASON IN THIS ENVIRONMENT
    //FOR OTHER THINGS GCHARS IS UPTO DATE AND USED
    addMisstake(String(quote).toLowerCase().includes(e.target.value) ? 0 : 1);
    guessingGame();
    console.log(e.target.className);
  };

  const createKeys = () => {
    return "abcdefghijklmnoprstuvwzxyq".split("").map((letter) => (
      <button
        className="m-2 p-2 bg-white text-black rounded-full"
        key={letter}
        value={letter}
        onClick={CreateGuess}
      >
        {letter}
      </button>
    ));
  };

  const resetGame = async () => {
    await api.get().then((res) => {
      setQuote(res.data.content);
    });
    //GETS SET() FROM HOOKS AND CLEARS IT
    //SETS IT AGAIN AS THE NEW VALUE
    let a = gchars;
    a.clear();
    setGchars(a);
    //RESETS REDUX STATES
    addQuote(quote);
    resetGuess();
    resetMisstakes();
    guessingGame();
  };

  return (
    <div className="bg-gray-900 text-white h-screen">
      <div className="flex justify-center items-center pt-10">
        <img
          src={props.images[misstakeCounter]}
          className="w-1/5 "
          alt="hangmangame"
        />
        <div className="pl-10 ">
          <div>Username: {player}</div>
          <div>Score: 103</div>
          <div>Time</div>
          <div>Misstakes {misstakeCounter} of 6 </div>
        </div>
      </div>
      <div className="flex mt-5">
        {}
        <div className="w-1/3 p-5 ix:w-screen m-auto bg-gray-900 text-center whitespace-pre-wrap">
          <div className=" align-center  text-[20px] ix:text-[12px]">
            {guessWords}
          </div>
        </div>
      </div>
      <div className="flex mt-5">
        <div className="m-auto bg-gray-900 text-center">
          <div className="grid grid-cols-7">{board}</div>
          <button
            className="w-1/2 mt-3 bg-black rounded text-white border-solid border-2 
          border-red"
            onClick={resetGame}
          >
            {" "}
            RESET{" "}
          </button>
        </div>
      </div>
    </div>
  );
}
