import axios from "axios";
import {
  FETCH_QUOTE_FAILURE,
  FETCH_QUOTE_SUCCESS,
  FETCH_QUOTE_REQUEST,
  SET_PLAYER_USERNAME,
  ADD_NEW_GUESS,
} from "./userTypes";

const api = axios.create({
  baseURL: "http://api.quotable.io/random",
});

const fetchQuoteRequest = () => {
  return {
    type: FETCH_QUOTE_REQUEST,
  };
};

const fetchQuoteSuccess = (quoteData) => {
  return {
    type: FETCH_QUOTE_SUCCESS,
    payload: quoteData,
  };
};

const fetchQuoteFailure = (error) => {
  return {
    type: FETCH_QUOTE_FAILURE,
    payload: error,
  };
};
const setupUsername = (username) => {
  return {
    type: SET_PLAYER_USERNAME,
    payload: username,
  };
};
const addNewGuess = (letter) => {
  return {
    type: ADD_NEW_GUESS,
    payload: letter,
  };
};
//it not pure , changes within the api
export const fetchQuote = () => {
  return (dispatch) => {
    dispatch(fetchQuoteRequest());
    api
      .get()
      .then((res) => {
        const data = res.data;
        dispatch(fetchQuoteSuccess(data));
      })
      .catch((error) => {
        const errorMsg = error.message;
        dispatch(fetchQuoteFailure(errorMsg));
      });
  };
};

export const changeUser = (username) => {
  return (dispatch) => {
    dispatch(setupUsername(username));
  };
};

export const addGuess = (letter) => {
  return (dispatch) => {
    dispatch(addNewGuess(letter));
  };
};
