import {
  FETCH_QUOTE_FAILURE,
  FETCH_QUOTE_SUCCESS,
  FETCH_QUOTE_REQUEST,
  SET_PLAYER_USERNAME,
  ADD_NEW_GUESS,
  INCREMENT_MISSTAKE,
  RESET_GAME_AND_DATA,
  UPDATE_SCORE,
  UPDATE_TIME,
} from "./userTypes";

const initialState = {
  loading: false,
  quote: "",
  id: "",
  username: "",
  time: 0,
  statusTime: false,
  score: 100,
  guessedKeys: new Set([]),
  misstakes: 0,
  error: "",
  lengthq: 0,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_QUOTE_REQUEST:
      return {
        ...state,
        loading: true,
      };
    case FETCH_QUOTE_SUCCESS:
      return {
        ...state,
        loading: false,
        quote: action.payload.content,
        id: action.payload._id,
        lengthq: action.payload.content.length,
        error: "",
      };
    case FETCH_QUOTE_FAILURE:
      return {
        ...state,
        loading: false,
        quote: "",
        error: action.payload,
      };
    case SET_PLAYER_USERNAME:
      return {
        ...state,
        username: action.payload,
      };
    case ADD_NEW_GUESS:
      return {
        ...state,
        guessedKeys: action.payload,
      };
    case INCREMENT_MISSTAKE:
      return {
        ...state,
        misstakes: state.misstakes + action.payload,
      };
    case RESET_GAME_AND_DATA:
      return {
        ...state,
        loading: false,
        quote: "",
        id: "",
        username: "",
        time: "",
        guessedKeys: new Set([]),
        misstakes: 0,
        score: 100,
        error: "",
        lengthq: 0,
        statusTime: false,
      };
    case UPDATE_SCORE:
      return {
        ...state,
        score: Math.round(100 / (action.payload + 1)),
      };
    case UPDATE_TIME:
      return {
        ...state,
        time: state.time + action.payload,
      };
    case STATUS_TIMER_FOR_TIME:
      return { ...state, statusTime: !state.statusTime };

    default:
      return state;
  }
};

export default userReducer;
