import React from "react";
import logo from "./images/logo.png";
import { useSelector, useDispatch } from "react-redux";
import { bindActionCreators } from "redux";
import { actionCreators } from "../state/index";
import { useState } from "react";

export default function UserSetup() {
  const [tempU, setTempU] = useState("");
  const dispatch = useDispatch();
  const account = useSelector((state) => state.player);
  const { setUsername } = bindActionCreators(actionCreators, dispatch);
  const setupUser = (e) => {
    e.preventDefault();
    setUsername(tempU);
    console.log(account);
  };
  return (
    <div className="bg-black flex justify-center h-screen items-center">
      <div className="w-full max-w-xs">
        <form
          className="bg-gray-900 border-solid border-2 border-orange-900 shadow-md rounded px-8 pt-6 pb-8 mb-4"
          onSubmit={setupUser}
        >
          <div className="text-white">{account}</div>
          <div className="flex justify-center items-center">
            <img
              src={logo}
              alt="logo"
              className="w-16 cursor-pointer bg-black rounded"
            />
          </div>

          <div className="mb-4 mt-4">
            <label
              className="block text-white text-sm font-bold mb-2 flex items-center justify-center"
              id="username"
            >
              ENTER YOUR USERNAME:
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              id="username"
              type="text"
              placeholder="username"
              onChange={(e) => setTempU(e.target.value)}
              required
            />
          </div>

          <div className="flex items-center justify-center">
            <button
              className="bg-orange-900 hover:bg-orange-300 hover:text-black text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              type="submit"
            >
              START GAME
            </button>
          </div>
        </form>
        <p className="text-center text-gray-500 text-xs">&copy;2022 Hangman</p>
      </div>
    </div>
  );
}
