import {
  FETCH_USERS_FAILURE,
  FETCH_USERS_SUCCESS,
  FETCH_USERS_REQUEST,
} from "./userTypes";

const initialState = {
  loading: false,
  quote: "",
  id: "",
  user: "",
  time: "",
  guessedKeys: [],
  misstakes: 0,
  error: "",
  length: 0,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_USERS_REQUEST:
      return {
        ...state,
        loading: true,
      };
    case FETCH_USERS_SUCCESS:
      return {
        ...state,
        loading: false,
        quote: action.payload.content,
        id: action.payload.id,
        length: action.payload.content.length,
        error: "",
      };
    case FETCH_USERS_FAILURE:
      return {
        ...state,
        loading: false,
        quote: "",
        error: action.payload,
      };
    default:
      return state;
  }
};

export default userReducer;
