import React, { useEffect, useState } from "react";

import UserSetup from "./UserSetup";
import { images } from "./images/images";

import { connect } from "react-redux";
import {
  fetchQuote,
  addGuess,
  addMisstake,
  resetGameAndData,
  updateScore,
  updateTime,
} from "../redux/user/userActions";

function HangmanLogic({
  userData,
  fetchQuote,
  addGuess,
  addMisstake,
  resetGameAndData,
  updateScore,
  updateTime,
}) {
  const [statusTime, setStatusTime] = useState(false);
  const [statusQuote, setStatusQuote] = useState(true);
  const [seconds, setSeconds] = useState(0);
  const createKeys = () => {
    return "abcdefghijklmnoprstuvwzxyq".split("").map((letter) => (
      <button
        className={
          userData.guessedKeys.has(letter)
            ? userData.guessedKeys.has(letter) &&
              userData.quote.toLowerCase().includes(letter)
              ? "p-2 bg-green-900 text-black rounded-full cursor:pointer"
              : "p-2 bg-yellow-500 text-black rounded-full cursor:pointer"
            : "p-2 bg-white text-black rounded-full cursor:pointer hover:bg-yellow-100"
        }
        key={letter}
        value={letter}
        disabled={userData.guessedKeys.has(letter)}
        onClick={handleGuess}
      >
        {letter}
      </button>
    ));
  };
  useEffect(() => {
    if (statusQuote) {
      setStatusQuote(false);
      fetchQuote();
    }

    if (statusTime) {
      let myInterval = setInterval(() => {
        setSeconds(seconds + 1000);
      }, 1000);
      return () => {
        clearInterval(myInterval);
      };
    }
  }, []);
  //FETCH QUOTE DONE

  //HANDLE GUESS DONE

  const handleGuess = (e) => {
    setStatusTime(true);
    //dodavanje novih elemenata u set([])
    //KORISTENJE SETA JER NEMA DUPLIKATA
    let guessedArray = userData.guessedKeys;
    guessedArray.add(e.target.value);
    addGuess(guessedArray);
    addMisstake(userData.quote.toLowerCase().includes(e.target.value) ? 0 : 1);
    /*console.log("Misstakes: ", userData.misstakes);
    updateScore(userData.misstakes);
    getUpdatedScore();
    console.log("New score:", userData.score);*/
  };

  //GUESSING GAME DONE
  const guessingGame = () => {
    console.log(userData.quote);
    let guessedLetters = userData.guessedKeys;

    let lines = userData.quote.split("").map((letter) => {
      if (/[a-zA-Z]/.test(letter)) {
        return guessedLetters.has(letter.toLowerCase()) ||
          guessedLetters.has(letter.toUpperCase())
          ? letter
          : " _ ";
      } else {
        return letter === " " ? "     " : letter;
      }
    });
    return lines;
  };
  const resetGame = () => {
    resetGameAndData();
    fetchQuote();
  };
  const guessingGame2 = () => {
    let guessedLetters = userData.guessedKeys;
    let lines = userData.quote.split("").map((letter) => {
      if (/[a-zA-Z]/.test(letter)) {
        return guessedLetters.has(letter.toLowerCase()) ||
          guessedLetters.has(letter.toUpperCase())
          ? letter
          : "_";
      } else {
        return letter === " " ? " " : letter;
      }
    });
    return lines;
  };
  const getPicture = () => {
    if (userData.misstakes === 6) {
      return 8;
    } else if (guessingGame2().join("") === userData.quote) {
      return 7;
    } else {
      return userData.misstakes;
    }
  };

  return (
    <div className="bg-gray-900 text-white h-screen">
      {!userData.username ? (
        <UserSetup />
      ) : (
        <div>
          <div className="flex justify-center items-center pt-10">
            <img
              src={images[getPicture()]}
              className="w-1/5 "
              alt="hangmangame"
            />
            <div className="pl-10 ">
              <div>Username: {userData.username}</div>
              <div>Score: {Math.round(100 / (1 + userData.misstakes))} </div>
              <div>Time: {statusTime / 1000}</div>
              <div>Misstakes: {userData.misstakes} of 6</div>
            </div>
          </div>
          {guessingGame2().join("") === userData.quote ? (
            <div className="flex mt-5">
              <div className="w-1/3 p-5 text-[20px] m-auto bg-gray-900 text-center whitespace-pre-wrap">
                YOU WON AND YOUR SCORE IS BEING SAVED!
                <div className="justify-center items-center p-4 rounded-full flex space-x-3">
                  <div className="w-7 h-7 mr-3 bg-white animate-spin "></div>
                </div>
              </div>
            </div>
          ) : guessingGame2().join("") !== userData.quote &&
            userData.misstakes === 6 ? (
            <div className="flex mt-5">
              <div className="w-1/3 p-5 text-[32px] m-auto bg-gray-900 text-center whitespace-pre-wrap">
                YOU LOST!
              </div>
            </div>
          ) : (
            <div>
              <div className="flex mt-5">
                <div className="w-1/3 p-5 m-auto bg-gray-900 text-center whitespace-pre-wrap">
                  <div className=" align-center  text-[20px] ix:text-[12px]">
                    {userData.quote ? guessingGame() : "Loading ..."}
                  </div>
                </div>
              </div>
              <div className="flex mt-5">
                <div className="m-auto bg-gray-900">
                  <div className="grid grid-cols-7 text-center">
                    {createKeys()}
                  </div>
                </div>
              </div>
            </div>
          )}

          <div className="w-screen mt-10 h-10 flex justify-center">
            <button
              className="w-1/5 bg-yellow-500 rounded border-solid border-2 border-black shadow drop-shadow hover:bg-yellow-400"
              onClick={resetGame}
            >
              {" "}
              RESET{" "}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    userData: state.user,
  };
};
