import React, { useEffect, useState } from "react";
import { updateTime } from "../redux/user/userActions";
import { connect } from "react-redux";
function App({ userData, updateTime }, props) {
  //const [seconds, setSeconds] = useState(0);
  const [status, setStatus] = useState(false);

  useEffect(() => {
    if (props.status) {
      let myInterval = setInterval(() => {
        updateTime(1000);
      }, 1000);
      return () => {
        clearInterval(myInterval);
      };
    }
  });

  return <div className="hidden">a</div>;
}
const mapStateToProps = (state) => {
  return {
    userData: state.user,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    updateTime: (value) => dispatch(updateTime(value)),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(TimeCounter);
