import React, { Component, useEffect } from "react";

import UserSetup from "./UserSetup";
import { images } from "./images/images";

import { connect } from "react-redux";
import { fetchQuote } from "../redux/user/userActions";

function HangmanLogic({ userData, fetchQuote }) {
  const createKeys = () => {
    return "abcdefghijklmnoprstuvwzxyq".split("").map((letter) => (
      <button
        className="m-2 p-2 bg-white text-black rounded-full"
        key={letter}
        value={letter}
      >
        {letter}
      </button>
    ));
  };
  useEffect(() => {
    fetchQuote();
  }, []);

  return (
    <div className="bg-gray-900 text-white h-screen w-screen">
      <div></div>
      {!userData.username ? (
        <UserSetup />
      ) : (
        <div className="flex justify-center items-center pt-10">
          <img src={images[0]} className="w-1/5 " alt="hangmangame" />
          <div className="pl-10 ">
            <div>Username: {userData.username}</div>
            <div>Score: </div>
            <div>Time: </div>
            <div>Misstakes: {userData.misstakes} of 6</div>
          </div>
          <div className="flex mt-5">
            <div className="w-1/3 p-5 ix:w-screen m-auto bg-gray-900 text-center whitespace-pre-wrap">
              <div className="keyboardbuttons align-center  text-[20px] ix:text-[12px]">
                {!userData.quote ? "Loading ..." : userData.quote}
              </div>
            </div>
          </div>
          <div className="flex mt-5">
            <div className="m-auto bg-gray-900 text-center">
              <div className="grid grid-cols-7">{createKeys()}</div>
            </div>
          </div>
          <div className="w-screen mt-10 h-10 flex justify-center">
            <button className="w-1/5 bg-orange-400 rounded border-solid border-2 border-white shadow drop-shadow">
              {" "}
              RESET{" "}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    userData: state.user,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    fetchQuote: () => dispatch(fetchQuote()),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(HangmanLogic);

