import React from "react";
import { useEffect, useState } from "react";
import start0 from "./images/start0.png";
import { useSelector } from "react-redux";

export default function HangmanLogic() {
  const player = useSelector((state) => state.player);
  const [board, setBoard] = useState([]);

  const handleGuess = () => {};

  const setupBoard = () => {
    return "abcdefghijklmnoprstuvwzxyq".split("").map((letter) => (
      <button
        className="m-2 p-2 bg-white text-black rounded-full"
        key={letter}
        value={letter}
      >
        {letter}
      </button>
    ));
  };

  useEffect(() => {
    setBoard(setupBoard());
  }, []);

  return (
    <div className="bg-gray-900 text-white h-screen w-screen">
      <div className="grid grid-cols-2 mt-10">
        <div className="w-6/12">
          <img src={start0} alt="hangmangame" />
        </div>
        <div className="stats">
          <div>Username: {player}</div>
          <div>Score: 103</div>
          <div>Time</div>
          <div>Misstakes 0 of 6 </div>
        </div>
      </div>
      <div>Guess sentence:</div>
      <div>___ _ _ _ __ ____ ___ __ ___ __ </div>
      <div className="grid grid-cols-7">{board}</div>
    </div>
  );
}

