import React, { Component } from "react";

import UserSetup from "./UserSetup";
import { images } from "./images/images";

export default class HangmanLogic extends Component {
  componentDidMount() {}

  render() {
    return (
      //<UserSetup />

      <div className="bg-gray-900 text-white h-screen">
        <div className="flex justify-center items-center pt-10">
          <img src={images[0]} className="w-1/5 " alt="hangmangame" />
          <div className="pl-10 ">
            <div>Username:</div>
            <div>Score: </div>
            <div>Time: </div>
            <div>Misstakes: </div>
          </div>
        </div>
        <div className="flex mt-5">
          {this.props.addTodo()}
          <div className="w-1/3 p-5 ix:w-screen m-auto bg-gray-900 text-center whitespace-pre-wrap">
            <div className="keyboardbuttons align-center  text-[20px] ix:text-[12px]"></div>
          </div>
        </div>
        <div className="flex mt-5">
          <div className="m-auto bg-gray-900 text-center">
            <div className="grid grid-cols-7">{}</div>
          </div>
        </div>
        <div class="w-screen h-10 flex justify-center">
          <button
            className="w-1/5 bg-orange-400 rounded border-solid border-2 border-white shadow drop-shadow"
            onClick={() => this.props.addTodo()}
          >
            {" "}
            RESET{" "}
          </button>
        </div>
      </div>
    );
  }
}

