import React from "react";
import { useEffect, useState } from "react";

import { images } from "./images";

import { useSelector, useDispatch } from "react-redux";
import { bindActionCreators } from "redux";
import { actionCreators } from "../state/index";

export default function HangmanLogic() {
  return (
    <div className="bg-gray-900 text-white h-screen">
      <div className="flex justify-center items-center pt-10">
        <img src={images[0]} className="w-1/5 " alt="hangmangame" />
        <div className="pl-10 ">
          <div>Username:</div>
          <div>Score: </div>
          <div>Time: </div>
          <div>Misstakes: </div>
        </div>
      </div>
      <div className="flex mt-5">
        {}
        <div className="w-1/3 p-5 ix:w-screen m-auto bg-gray-900 text-center whitespace-pre-wrap">
          <div className="keyboardbuttons align-center  text-[20px] ix:text-[12px]"></div>
        </div>
      </div>
      <div className="flex mt-5">
        <div className="m-auto bg-gray-900 text-center">
          <div className="grid grid-cols-7">{}</div>
        </div>
      </div>
      <button className="flex justify-center items-center absolute ml-10 bg-orange-500">
        {" "}
        RESET{" "}
      </button>
    </div>
  );
}

